function getStargazers(e){return fetch(new Request(`https://api.github.com/repos/${e}`)).then(e=>e.json()).then(e=>e.stargazers_count)}for(const e of document.querySelectorAll(".project-box")){const t=e.querySelector(".link").getAttribute("href"),r=/github\.com\/(.*\/.*)$/.exec(t);2===r.length&&getStargazers(r[1]).then(t=>{t>=1e3&&(t=`${(t/1e3).toFixed(1)}k`),e.querySelector(".stargazers").innerHTML=`⭐ ${null!=t?t:0}`}).catch(e=>console.error(e))}